import React, { useContext, useEffect } from "react";
import { DataContext } from "../context";
import { Link } from "react-router-dom";
import Colors from "./colors";
import "../css/Details.css";
import "../css/Cart.css";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { toast } from "react-toastify";

import "react-toastify/dist/ReactToastify.css";
toast.configure();
const Cart = () => {
  const {
    cart,
    handleIncrement,
    handleDecrement,
    handleRemove,
    total,
    handleTotal,
  } = useContext(DataContext);

  //Pay With Card HandleToken
  async function handleToken(token, addresses) {
    const response = await axios.post(
      "https://ry7v05l6on.sse.codesandbox.io/checkout",
      { token, cart }
    );
    const { status } = response.data;
    console.log("Response:", response.data);
    if (status === "success") {
      toast("Success! Check email for details", { type: "success" });
    } else {
      toast("Something went wrong", { type: "error" });
    }
  }

  //Added to Total in the cart
  useEffect(() => {
    handleTotal();
  }, []);

  //Without product showing empty cart
  if (cart.length === 0) {
    return (
      <h2 style={{ textAlign: "center" }}> Your Cart is Currently Empty</h2>
    );
  } else {
    return (
      <div>
        {cart.map((item) => (
          <div className="details cart" key={item._id}>
            <img src={item.src} alt="" />
            <div className="box">
              <div className="row">
                <h2>{item.title}</h2>
                <span>£{item.price * item.count}</span>
              </div>

              <Colors colors={item} />

              <p>{item.description}</p>
              <p>{item.content}</p>
              <div className="amount">
                <button
                  className="count"
                  onClick={() => handleDecrement(item._id)}
                >
                  -
                </button>
                <span>{item.count}</span>
                <button
                  className="count"
                  onClick={() => handleIncrement(item._id)}
                >
                  +
                </button>
              </div>
            </div>
            <div className="delete" onClick={() => handleRemove(item._id)}>
              Remove
            </div>
          </div>
        ))}
        <div className="total">
          <Link to="/product" style={{ background: "crimson" }}>
            Continue Shopping
          </Link>
          <div className="checkout">
            {/* <Link to="/checkout">Checkout</Link> */}
          </div>
          <h3>Total: £{total}</h3>
        </div>
        <div className="pay-with-card">
          <div className="container">
            <StripeCheckout
              stripeKey="pk_test_4TbuO6qAW2XPuce1Q6ywrGP200NrDZ2233"
              token={handleToken}
              amount={total * 100}
              name="NIKE"
              billingAddress
              shippingAddress
            />
          </div>
        </div>
      </div>
    );
  }
};

export default Cart;
