  const { cart } = useContext(DataContext);
  //   const product = products.find((x) => x._id === props.match.params.id);

  return (
    <div>
      <div className="details">
        <img src={cart.src} alt="" />
        <div className="box">
          <div className="row">
            <h2>{cart.title}</h2>
            <span>Â£{cart.price}</span>
          </div>

          <p>{cart.description}</p>
          <p>{cart.content}</p>
          <Link to="/cart" className="cart">
            Add to cart
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Cart;
