import React, { useState } from "react";
import axios from "axios";
import Header from "../header";
const Contact = () => {
  const [user, setUser] = useState({
    name: "",
    lastname: "",
    email: "",
    message: "",
    sent: false,
    buttonText: "Send Message",
  });
  const [err, setErr] = useState(" ");

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
    setErr("");
  };
  const formSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/api/forma", {
        name: user.name,
        lastname: user.lastname,
        email: user.email,
        message: user.message,
      });
      setUser({ sent: true });

      setUser({ name: "", email: "", password: "", password2: "" });
      console.log(user.name);

      setErr(res.data.msg);
    } catch (err) {
      err.response.data.msg && setErr(err.response.data.msg);
    }
  };

  return (
    <div>
      <Header />
      <div className="row mt-5">
        <div className="col-md-6 m-auto">
          <div className="card card-body">
            <div className=" alert-warning alert-dismissible fade show err-btn">
              {err}
            </div>

            <h1 className="text-center mb-3">
              <i className="fas fa-user-plus"></i> Join us
            </h1>
            <form onSubmit={formSubmit}>
              <div className="form-group">
                <label>Name</label>
                <input
                  type="name"
                  id="register-name"
                  name="name"
                  className="form-control"
                  placeholder="Enter Name"
                  value={user.name}
                  onChange={onChangeInput}
                  username={user.name}
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  id="register-email"
                  name="email"
                  className="form-control"
                  placeholder="Enter Email"
                  value={user.email}
                  onChange={onChangeInput}
                />
              </div>
              <div className="form-group">
                <label>lastname</label>
                <input
                  type="lastname"
                  id="register-password"
                  name="lastname"
                  className="form-control"
                  placeholder=" lastname"
                  value={user.lastname}
                  onChange={onChangeInput}
                />
              </div>
              <div className="form-group">
                <label>message</label>
                <textarea
                  name="message"
                  value={user.message}
                  id=""
                  cols="30"
                  rows="5"
                  placeholder="your message..."
                  onChange={onChangeInput}
                ></textarea>
              </div>

              <button
                type="submit"
                className="btn btn-primary btn-block bg-primary"
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
