import React, { useState } from "react";
import axios from "axios";
import "../css/style.css";

const Form = () => {
  const [user, setUser] = useState({
    name: "",
    lastname: "",
    email: "",
    message: "",
    sent: false,
    buttonText: "Send Message",
  });
  const [err, setErr] = useState(" ");

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
    setErr("");
  };
  const formSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/api/forma", {
        name: user.name,
        lastname: user.lastname,
        email: user.email,
        message: user.message,
      });
      setUser({ sent: true });

      setUser({ name: "", email: "", password: "", password2: "" });
      console.log(user.name);

      setErr(res.data.msg);
    } catch (err) {
      err.response.data.msg && setErr(err.response.data.msg);
    }
  };
  // const [user, setUser] = useState({
  //   name: "",
  //   lastname: "",
  //   email: "",
  //   message: "",
  //   sent: false,
  //   buttonText: "Send Message",
  // });

  // const onChangeInput = (e) => {
  //   const { name, value } = e.target;
  //   setUser({ ...user, [name]: value });
  // };
  // const formSubmit = async (e) => {
  //   e.preventDefault();
  //   let data = {
  //     name: user.name,
  //     lastname: user.lastname,
  //     email: user.email,
  //     message: user.message,
  //   };
  //   try {
  //     const res = await axios.post("/api/forma", data);
  //     setUser({ sent: true });
  //     setUser({ name: "", email: "", lastname: "", message: "" });
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };
  // for reseting the form data

  return (
    <div className="container">
      <form onSubmit={formSubmit}>
        <div className="singleItem">
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={user.name}
            className="name"
            onChange={onChangeInput}
            placeholder="your name..."
          />
        </div>

        <div className="singleItem">
          <label>Lastname</label>
          <input
            type="text"
            name="lastname"
            value={user.lastname}
            className="lastname"
            onChange={onChangeInput}
            placeholder="your lastname..."
          />
        </div>

        <div className="singleItem">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={user.email}
            className="email"
            onChange={onChangeInput}
            placeholder="your email..."
            required
          />
        </div>

        <div className="textArea singleItem">
          <label>Message</label>
          <textarea
            name="message"
            id=""
            value={user.message}
            cols="30"
            rows="5"
            placeholder="your message..."
            onChange={onChangeInput}
          ></textarea>
        </div>

        <div className={user.sent ? "msg msgAppear" : "msg"}>
          Message has been sent
        </div>
        <div className="btn">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
