import React, { useState } from "react";
import Menu from "./svg/bars-solid.svg";
import Close from "./svg/times-solid.svg";
import CartIcon from "./svg/shopping-cart-solid.svg";
import { Link } from "react-router-dom";
import "./css/Header.css";

const Header = () => {
  const openMenuToggle = () => {
    document.querySelector(".nav").classList.add("open");
  };
  const removeMenuToggle = () => {
    document.querySelector(".nav").classList.remove("open");
  };
  return (
    <div>
      <header>
        <div className="menu" onClick={openMenuToggle}>
          <img src={Menu} alt=" Menu" width="20" />
        </div>
        <div className="logo">
          <h1>
            <Link to="/">Nike</Link>
          </h1>
        </div>
        <nav className="nav">
          <ul className="toggle">
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/product">Product</Link>
            </li>
            <li>
              <Link to="/contact">Contact</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/login">Login / Register</Link>
            </li>
            <li className="close" onClick={removeMenuToggle}>
              <img src={Close} alt="" width="15" />
            </li>
          </ul>
          <div className="nav-cart">
            <span>0</span>

            <Link to="/cart">
              <img src={CartIcon} alt=" CartIcon" width="20" />
            </Link>
          </div>
        </nav>
      </header>
    </div>
  );
};

export default Header;
