import React, { useState, useContext, useEffect } from "react";
import { DataContext } from "../context";
import { Link } from "react-router-dom";
import Colors from "./colors";
import "../css/Details.css";
import "../css/Cart.css";

import { toast } from "react-toastify";

import "react-toastify/dist/ReactToastify.css";
import StripeCheckout from "react-stripe-checkout";
toast.configure();

const Cart = () => {
  const {
    cart,
    handleIncrement,
    handleDecrement,
    handleRemove,
    total,
    handleTotal,
    removeAll,
  } = useContext(DataContext);

  //Pay With Card HandleToken
  const handleToken = (token) => {
    const body = {
      token,
      cart,
      total,
    };
    const headers = {
      "Content-Type": "application/json",
    };
    return fetch(`http://localhost:8282/payment`, {
      method: "POST",
      headers,
      body: JSON.stringify(body),
    })
      .then((response) => {
        toast(" Success! Check email for details", { type: "success" });

        // alert("Success! Check email for details");
        removeAll();

        console.log(response);
      })
      .catch(() => {
        toast("Something went wrong", { type: "error" });
      });
  };

  //Added to Total in the cart
  useEffect(() => {
    handleTotal();
  }, []);

  //Without product showing empty cart
  if (cart.length === 0) {
    return (
      <h2 style={{ textAlign: "center" }}> Your Cart is Currently Empty</h2>
    );
  } else {
    return (
      <div className="remove">
        {cart.map((item) => (
          <div className="details cart" key={item._id}>
            <img src={item.src} alt="" />
            <div className="box">
              <div className="row">
                <h2>{item.title}</h2>
                <span>£{item.price * item.count}</span>
              </div>

              <Colors colors={item} />

              <p>{item.description}</p>
              <p>{item.content}</p>
              <div className="amount">
                <button
                  className="count"
                  onClick={() => handleDecrement(item._id)}
                >
                  -
                </button>
                <span>{item.count}</span>
                <button
                  className="count"
                  onClick={() => handleIncrement(item._id)}
                >
                  +
                </button>
              </div>
              <div className="checkout">
                <h3>Total: £{total}</h3>
              </div>
            </div>

            <div className="delete" onClick={() => handleRemove(item._id)}>
              Remove
            </div>
          </div>
        ))}
        <div className="total">
          <Link to="/product" style={{ background: "crimson" }}>
            Continue Shopping
          </Link>
        </div>
        <div className="pay-with-card">
          <div className="container">
            <StripeCheckout
              stripeKey="pk_test_51HAlmnIfOyLdLwzAz2piAWlcCJcAPATSEkCyxcUG7m1F80xXqr4wd8FoFWqYEfXUUEEgDGBx5G9aXG4IenAw46fh00x1Qgnr7v"
              token={handleToken}
              name="NIKE"
              billingAddress
              shippingAddress
            />
          </div>
        </div>
      </div>
    );
  }
};

export default Cart;
